import React from "react";
import { useContext } from "react";
import { Link } from "react-router-dom";
import { CartContext } from "./context/CartContext";

const Cart = () => {
  const { cart, cartTotal, removeItem ,clear } = useContext(CartContext);

  if (cartTotal() === 0) {
    return (
      <div className="container">
        <div className="row m-5">
          <div className="col-md-12 text-center">
            <div classname="alert alert-danger" role="alert">
              No se encontraron productos en el Carrito
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="row my-5">
        <div className="col-md-12">
          <table class="table">
                <thead>
                <tr>
                    <th scope="col">&nbsp;</th>
                    <th scope="col">Producto</th>
                    <th scope="col">Cantidad</th>
                    <th scope="col">Precio</th>
                    <th scope="col">&nbsp;</th>
                </tr>
                </thead>
                <tbody>
                    {cart.map(item => (

                        <tr key={item.id}>
                            <td><img src={item.imagen} alt={item.nombre} width={80}></img></td>
                            <td className="aling-">{item.nombre}</td>
                            <td>{item.quantity}</td>
                            <td>${item.quantity * item.precio}</td>
                            <td><Link onClick={() => {removeItem(item.id)}}><img src={"../public/trash-fill.svg"} alt={"Eliminar producto"} width={32}></img></Link></td>

                            </tr>
                    ))

                    }
                </tbody>
            </table>
            </div>
      </div>
    </div>
  );
};

export default Cart;
